    public void actualizarTotales(int numfacturas,double base, double iva, double subtotal, double baseni, double retenc, double total){
        this.lblBase.setText(base+"");
        this.lblIVA.setText(iva+"");
        this.lblBaseNI.setText(baseni+"");
        this.lblRetenc.setText(retenc+"");
        this.lblTotal.setText(total+"");
        this.lblnumfacturas.setText(numfacturas+"");
    }
    
